mapper.select:
SELECT COUNT(LIKE_NO) AS CNT
FROM BLOG_LIKE
WHERE BLOG_NO=#{blog_no} AND USER_ID=#{user_id}

mapper.update:
UPDATE BLOG_LIKE
SET LIKE_CHECK = LIKE_CHECK +1
WHERE USER_ID = #{user_id}
AND BLOG_NO = #{blog_no}

UPDATE BLOG_LIKE
SET LIKE_CHECK = 0
WHERE USER_ID = #{user_id}
AND BLOG_NO = #{blog_no}

mapper.select:
SELECT LIKE_NO, BLOG_NO, USER_ID, LIKE_CHECK
FROM BLOG_LIKE
WHERE BLOG_NO=#{blog_no}
AND USER_ID=#{user_id}
